// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/IPokeNFT.sol";
import "./interfaces/IPokeToken.sol";

contract PokeMarketplace {
    IPokeNFT nft;

    struct Listing {
        uint256 nftPrize;
        address owner;
    }

    mapping(address => mapping(uint256 => Listing)) private listings;
    mapping(address => uint256) private proceeds;

    modifier notListed(uint256 tokenId) {
        Listing memory listing = listings[address(nft)][tokenId];
        require(listing.nftPrize == 0, "Already listed");
        _;
    }

    modifier isListed(uint256 tokenId) {
        Listing memory listing = listings[address(nft)][tokenId];
        require(listing.nftPrize > 0, "Not listed");
        _;
    }

    modifier isOwner(uint256 tokenId, address spender) {
        address owner = nft.ownerOf(tokenId);
        require(spender == owner, "Not owner");
        _;
    }

    constructor(address nftAddress) {
        nft = IPokeNFT(nftAddress);
    }
}

